mapper.select:
select online_shopping.shop.id, online_shopping.shop.title, online_shopping.shop.price, online_shopping.shop.discount_price, online_shopping.shop.slider_shows, online_shopping.spu.description, c.status as cart_status, wl.status as wish_list_status from online_shopping.shop left join online_shopping.spu on shop.spu_id = spu.id left join ( select status,shop_id from online_shopping.cart
user_id = #{userId} and deleted = 0
deleted = 0
) c on shop.id = c.shop_id left join ( select status,shop_id from online_shopping.wish_list
user_id = #{userId} and deleted = 0
deleted = 0
) wl on shop.id = wl.shop_id where shop.deleted = 0 and spu.status = 'SHELVES' and shop.status = 'SHELVES' limit #{offset},#{size}
